mapper.select:



    AND dict_type = #{dictType}


    AND dict_label like concat('%', #{dictLabel}, '%')


    AND status = #{status}


order by dict_sort asc

mapper.update:
update sys_dict_data
dict_sort = #{dictSort}, dict_label = #{dictLabel}, dict_value = #{dictValue}, dict_type = #{dictType}, css_class = #{cssClass}, list_class = #{listClass}, is_default = #{isDefault}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where dict_code = #{dictCode}
