mapper.select:
select 

from sys_job
where job_id = #{jobId,jdbcType=BIGINT}
  and job_name = #{jobName,jdbcType=VARCHAR}
  and job_group = #{jobGroup,jdbcType=VARCHAR}

mapper.delete:
delete from sys_job
where job_id = #{jobId,jdbcType=BIGINT}
  and job_name = #{jobName,jdbcType=VARCHAR}
  and job_group = #{jobGroup,jdbcType=VARCHAR}

mapper.update:
update sys_job
job_name = #{jobName}, job_group = #{jobGroup}, invoke_target = #{invokeTarget}, cron_expression = #{cronExpression}, misfire_policy = #{misfirePolicy}, concurrent = #{concurrent}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where job_id = #{jobId}
