mapper.select:

where r.del_flag = '0'

  AND r.role_id = #{roleId}


  AND r.role_name like concat('%', #{roleName}, '%')


  AND r.status = #{status}


  AND r.role_key like concat('%', #{roleKey}, '%')


  and date_format(r.create_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


  and date_format(r.create_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


${params.dataScope}
order by r.role_sort

mapper.update:
update sys_role
role_name = #{roleName}, role_key = #{roleKey}, role_sort = #{roleSort}, data_scope = #{dataScope}, menu_check_strictly = #{menuCheckStrictly}, dept_check_strictly = #{deptCheckStrictly}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where role_id = #{roleId}
